mapper.insert:
INSERT INTO users (
    email, password, name, nickname, gender, user_type, status, profile_image, created_at, updated_at
) VALUES (
    #{email}, #{password}, #{name}, #{nickname}, #{gender}, #{userType}, #{status},
    #{profileImage}, #{createdAt}, #{updatedAt}
)

mapper.select:
SELECT EXISTS(
    SELECT 1 FROM users WHERE email = #{email}
)

SELECT COUNT(*) 
  FROM users
 WHERE nickname = #{nickname}

SELECT EXISTS (
    SELECT 1
      FROM trainer_details
     WHERE user_id = #{userId}
)
